import React, { useState, useEffect } from 'react';

const PaymentForm = () => {
  const [students, setStudents] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [showResults, setShowResults] = useState(false);
  const [selectedStudent, setSelectedStudent] = useState(null);
  const [formData, setFormData] = useState({
    studentName: '',
    amount: '',
    paymentDate: '',
    paymentMonth: '',
    paymentType: 'cash'
  });

  useEffect(() => {
    const loadedStudents = JSON.parse(localStorage.getItem('students') || '[]');
    setStudents(loadedStudents);
  }, []);

  const filteredStudents = students.filter(student =>
    student.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const handleStudentSelect = (student) => {
    setSelectedStudent(student);
    setFormData({
      ...formData,
      studentName: student.name
    });
    setSearchTerm(student.name);
    setShowResults(false);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!selectedStudent) {
      alert('학생을 선택해주세요.');
      return;
    }
    const payments = JSON.parse(localStorage.getItem('payments') || '[]');
    const newPayment = {
      id: Date.now(),
      ...formData,
      createdAt: new Date().toISOString()
    };
    payments.push(newPayment);
    localStorage.setItem('payments', JSON.stringify(payments));

    setFormData({
      studentName: '',
      amount: '',
      paymentDate: '',
      paymentMonth: '',
      paymentType: 'cash'
    });
    alert('결제가 등록되었습니다.');
  };

  return (
    <form onSubmit={handleSubmit} className="bg-white p-6 rounded-lg shadow">
      <h2 className="text-xl font-bold mb-4">결제 등록</h2>
      <div className="space-y-4">
        <div className="relative">
          <label className="block mb-1">학생 검색</label>
          <input
            type="text"
            value={searchTerm}
            onChange={(e) => {
              setSearchTerm(e.target.value);
              setShowResults(true);
            }}
            className="w-full p-2 border rounded"
            placeholder="학생 이름을 입력하세요"
          />
          
          {/* 검색 결과 드롭다운 */}
          {showResults && searchTerm && (
            <div className="absolute z-10 w-full bg-white border rounded-md mt-1 max-h-60 overflow-auto shadow-lg">
              {filteredStudents.map(student => (
                <div
                  key={student.id}
                  className="px-4 py-2 hover:bg-gray-100 cursor-pointer"
                  onClick={() => handleStudentSelect(student)}
                >
                  <div className="font-medium">{student.name}</div>
                  <div className="text-sm text-gray-500">{student.subject}</div>
                </div>
              ))}
              {filteredStudents.length === 0 && (
                <div className="px-4 py-2 text-gray-500">검색 결과가 없습니다</div>
              )}
            </div>
          )}
        </div>

        <div>
          <label className="block mb-1">결제 금액</label>
          <input
            type="number"
            value={formData.amount}
            onChange={(e) => setFormData({...formData, amount: e.target.value})}
            className="w-full p-2 border rounded"
            required
          />
        </div>
        <div>
          <label className="block mb-1">결제일</label>
          <input
            type="date"
            value={formData.paymentDate}
            onChange={(e) => setFormData({...formData, paymentDate: e.target.value})}
            className="w-full p-2 border rounded"
            required
          />
        </div>
        <div>
          <label className="block mb-1">수강월</label>
          <input
            type="month"
            value={formData.paymentMonth}
            onChange={(e) => setFormData({...formData, paymentMonth: e.target.value})}
            className="w-full p-2 border rounded"
            required
          />
        </div>
        <div>
          <label className="block mb-1">결제 방식</label>
          <select
            value={formData.paymentType}
            onChange={(e) => setFormData({...formData, paymentType: e.target.value})}
            className="w-full p-2 border rounded"
            required
          >
            <option value="cash">현금</option>
            <option value="card">카드</option>
            <option value="transfer">계좌이체</option>
          </select>
        </div>
        <button
          type="submit"
          className="w-full bg-blue-500 text-white py-2 px-4 rounded hover:bg-blue-600"
        >
          결제 등록
        </button>
      </div>
    </form>
  );
};

export default PaymentForm;
