import React, { useState } from 'react';

const StudentForm = () => {
  const [formData, setFormData] = useState({
    name: '',
    phone: '',
    subject: '',
    startDate: ''
  });

  const handleSubmit = (e) => {
    e.preventDefault();
    // localStorage에 학생 정보 저장 로직
    const students = JSON.parse(localStorage.getItem('students') || '[]');
    const newStudent = {
      id: Date.now(),
      ...formData,
      createdAt: new Date().toISOString()
    };
    students.push(newStudent);
    localStorage.setItem('students', JSON.stringify(students));
    
    // 폼 초��화
    setFormData({
      name: '',
      phone: '',
      subject: '',
      startDate: ''
    });
    alert('학생이 등록되었습니다.');
  };

  return (
    <form onSubmit={handleSubmit} className="bg-white p-6 rounded-lg shadow">
      <div className="space-y-4">
        <div>
          <label className="block mb-1">이름</label>
          <input
            type="text"
            value={formData.name}
            onChange={(e) => setFormData({...formData, name: e.target.value})}
            className="w-full p-2 border rounded"
            required
          />
        </div>
        <div>
          <label className="block mb-1">연락처</label>
          <input
            type="tel"
            value={formData.phone}
            onChange={(e) => setFormData({...formData, phone: e.target.value})}
            className="w-full p-2 border rounded"
            required
          />
        </div>
        <div>
          <label className="block mb-1">수강 과목</label>
          <select
            value={formData.subject}
            onChange={(e) => setFormData({...formData, subject: e.target.value})}
            className="w-full p-2 border rounded"
            required
          >
            <option value="">과목 선택</option>
            <option value="piano">피아노</option>
            <option value="violin">바이올린</option>
            <option value="guitar">기타</option>
            <option value="drum">드럼</option>
          </select>
        </div>
        <div>
          <label className="block mb-1">등록일</label>
          <input
            type="date"
            value={formData.startDate}
            onChange={(e) => setFormData({...formData, startDate: e.target.value})}
            className="w-full p-2 border rounded"
            required
          />
        </div>
        <button
          type="submit"
          className="w-full bg-blue-500 text-white py-2 px-4 rounded hover:bg-blue-600"
        >
          등록하기
        </button>
      </div>
    </form>
  );
};

export default StudentForm;
