import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { Bars3Icon, XMarkIcon } from '@heroicons/react/24/outline';
import ThemeToggle from '../ui/ThemeToggle';

const Layout = ({ children }) => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);  // 추가된 state

  const navItems = [
    { title: '대시보드', path: '/', className: 'text-emerald-600 hover:text-emerald-700' },
    { title: '학생등록', path: '/student-registration', className: 'text-sky-600 hover:text-sky-700' },
    { title: '학생관리', path: '/student-management', className: 'text-violet-600 hover:text-violet-700' },
    { title: '수업관리', path: '/class-management', className: 'text-amber-600 hover:text-amber-700' },
    { title: '결제관리', path: '/payment-management', className: 'text-rose-600 hover:text-rose-700' },
  ];

  return (
    <div className="min-h-screen bg-background">
      <nav className="border-b bg-white/80 backdrop-blur supports-[backdrop-filter]:bg-white/80 sticky top-0 z-40">
        <div className="container px-4 mx-auto">
          <div className="flex items-center justify-between h-16">
            <Link 
              to="/" 
              className="text-2xl font-bold text-primary hover:text-primary/80 transition-colors"
            >
              MUSIC ACADEMY
            </Link>

            {/* Desktop Navigation */}
            <div className="hidden md:flex items-center space-x-8">
              {navItems.map(item => (
                <Link
                  key={item.path}
                  to={item.path}
                  className={`${item.className} font-medium transition-colors`}
                >
                  {item.title}
                </Link>
              ))}
              <ThemeToggle />
            </div>

            {/* Mobile menu button */}
            <button
              className="md:hidden p-2"
              onClick={() => setIsMenuOpen(!isMenuOpen)}
            >
              {isMenuOpen ? (
                <XMarkIcon className="h-6 w-6" />
              ) : (
                <Bars3Icon className="h-6 w-6" />
              )}
            </button>
          </div>
        </div>

        {/* Mobile Navigation */}
        {isMenuOpen && (
          <div className="md:hidden border-t">
            <div className="container px-4 py-3">
              {navItems.map(item => (
                <Link
                  key={item.path}
                  to={item.path}
                  className="block py-2 text-primary hover:text-primary/80 transition-colors"
                  onClick={() => setIsMenuOpen(false)}
                >
                  {item.title}
                </Link>
              ))}
              <div className="py-2">
                <ThemeToggle />
              </div>
            </div>
          </div>
        )}
      </nav>

      <main className="container mx-auto px-4 py-8">
        {children}
      </main>
    </div>
  );
};

export default Layout;
