import React, { useState, useEffect } from 'react';

const StudentList = () => {
  const [students, setStudents] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [payments, setPayments] = useState([]);
  const [classes, setClasses] = useState([]);

  useEffect(() => {
    const loadData = () => {
      const loadedStudents = JSON.parse(localStorage.getItem('students') || '[]');
      const loadedPayments = JSON.parse(localStorage.getItem('payments') || '[]');
      const loadedClasses = JSON.parse(localStorage.getItem('classes') || '[]');
      
      const enrichedStudents = loadedStudents.map(student => ({
        ...student,
        payments: loadedPayments.filter(p => p.studentName === student.name),
        classes: loadedClasses.filter(c => c.studentName === student.name)
      }));

      setStudents(enrichedStudents);
      setPayments(loadedPayments);
      setClasses(loadedClasses);
    };

    loadData();
  }, []);

  const handleDelete = (id) => {
    if (window.confirm('정말 삭제하시겠습니까?')) {
      const updatedStudents = students.filter(student => student.id !== id);
      localStorage.setItem('students', JSON.stringify(updatedStudents));
      setStudents(updatedStudents);
    }
  };

  const filteredStudents = students.filter(student =>
    student.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="bg-white p-6 rounded-lg shadow">
      <div className="mb-4">
        <input
          type="text"
          placeholder="학생 검색..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          className="w-full p-2 border rounded"
        />
      </div>
      <div className="overflow-x-auto">
        <table className="w-full border-collapse">
          <thead>
            <tr className="bg-gray-50">
              <th className="w-[16.66%] px-4 py-3 text-center text-sm font-semibold text-gray-600 border-b">이름</th>
              <th className="w-[16.66%] px-4 py-3 text-center text-sm font-semibold text-gray-600 border-b">과목</th>
              <th className="w-[16.66%] px-4 py-3 text-center text-sm font-semibold text-gray-600 border-b">수업 횟수</th>
              <th className="w-[16.66%] px-4 py-3 text-center text-sm font-semibold text-gray-600 border-b">최근 결제</th>
              <th className="w-[16.66%] px-4 py-3 text-center text-sm font-semibold text-gray-600 border-b">미납금</th>
              <th className="w-[16.66%] px-4 py-3 text-center text-sm font-semibold text-gray-600 border-b">관리</th>
            </tr>
          </thead>
          <tbody>
            {filteredStudents.map(student => (
              <tr key={student.id} className="border-b hover:bg-gray-50">
                <td className="px-4 py-3 text-center text-sm text-gray-900">{student.name}</td>
                <td className="px-4 py-3 text-center text-sm text-gray-900">{student.subject}</td>
                <td className="px-4 py-3 text-center text-sm text-gray-900">{student.classes?.length || 0}</td>
                <td className="px-4 py-3 text-center text-sm text-gray-900">
                  {student.payments?.length > 0 
                    ? new Date(student.payments[student.payments.length - 1].paymentDate).toLocaleDateString()
                    : '-'}
                </td>
                <td className="px-4 py-3 text-center text-sm text-gray-900">
                  <span className={`inline-block px-2 py-1 rounded-full text-xs font-semibold
                    ${student.payments?.length > 0 
                      ? 'bg-green-100 text-green-800' 
                      : 'bg-red-100 text-red-800'}`}>
                    {student.payments?.length > 0 ? '완납' : '미납'}
                  </span>
                </td>
                <td className="px-4 py-3 text-center text-sm text-gray-900">
                  <button className="text-blue-600 hover:text-blue-800 px-2">상세</button>
                  <button className="text-red-600 hover:text-red-800 px-2 ml-2">삭제</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default StudentList;
