import React, { useState } from 'react';

const PaymentForm = () => {
  const [formData, setFormData] = useState({
    studentName: '',
    amount: '',
    paymentDate: '',
    paymentMonth: '',
    paymentType: 'cash'
  });

  const handleSubmit = (e) => {
    e.preventDefault();
    const payments = JSON.parse(localStorage.getItem('payments') || '[]');
    const newPayment = {
      id: Date.now(),
      ...formData,
      createdAt: new Date().toISOString()
    };
    payments.push(newPayment);
    localStorage.setItem('payments', JSON.stringify(payments));

    setFormData({
      studentName: '',
      amount: '',
      paymentDate: '',
      paymentMonth: '',
      paymentType: 'cash'
    });
    alert('결제가 등록되었습니다.');
  };

  return (
    <form onSubmit={handleSubmit} className="bg-white p-6 rounded-lg shadow">
      <h2 className="text-xl font-bold mb-4">결제 등록</h2>
      <div className="space-y-4">
        <div>
          <label className="block mb-1">학생 이름</label>
          <input
            type="text"
            value={formData.studentName}
            onChange={(e) => setFormData({...formData, studentName: e.target.value})}
            className="w-full p-2 border rounded"
            required
          />
        </div>
        <div>
          <label className="block mb-1">결제 금액</label>
          <input
            type="number"
            value={formData.amount}
            onChange={(e) => setFormData({...formData, amount: e.target.value})}
            className="w-full p-2 border rounded"
            required
          />
        </div>
        <div>
          <label className="block mb-1">결제일</label>
          <input
            type="date"
            value={formData.paymentDate}
            onChange={(e) => setFormData({...formData, paymentDate: e.target.value})}
            className="w-full p-2 border rounded"
            required
          />
        </div>
        <div>
          <label className="block mb-1">수강월</label>
          <input
            type="month"
            value={formData.paymentMonth}
            onChange={(e) => setFormData({...formData, paymentMonth: e.target.value})}
            className="w-full p-2 border rounded"
            required
          />
        </div>
        <div>
          <label className="block mb-1">결제 방식</label>
          <select
            value={formData.paymentType}
            onChange={(e) => setFormData({...formData, paymentType: e.target.value})}
            className="w-full p-2 border rounded"
            required
          >
            <option value="cash">현금</option>
            <option value="card">카드</option>
            <option value="transfer">계좌이체</option>
          </select>
        </div>
        <button
          type="submit"
          className="w-full bg-blue-500 text-white py-2 px-4 rounded hover:bg-blue-600"
        >
          결제 등록
        </button>
      </div>
    </form>
  );
};

export default PaymentForm;
