import React from 'react';
import { useLocalStorage } from '../../hooks/useLocalStorage';

const DashboardOverview = () => {
  const [students] = useLocalStorage('students', []);
  const [payments] = useLocalStorage('payments', []);
  const [classes] = useLocalStorage('classes', []);

  const calculateStats = () => {
    const currentMonth = new Date().getMonth() + 1;
    const monthlyPayments = payments.filter(p => new Date(p.paymentDate).getMonth() + 1 === currentMonth);
    const totalRevenue = monthlyPayments.reduce((sum, p) => sum + Number(p.amount), 0);
    
    const subjectDistribution = students.reduce((acc, student) => {
      acc[student.subject] = (acc[student.subject] || 0) + 1;
      return acc;
    }, {});

    return {
      totalStudents: students.length,
      totalClasses: classes.length,
      monthlyRevenue: totalRevenue,
      subjectDistribution
    };
  };

  const stats = calculateStats();

  return (
    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
      <div className="bg-white p-6 rounded-lg shadow">
        <h3 className="text-lg font-semibold">전체 학생</h3>
        <p className="text-3xl font-bold text-blue-600">{stats.totalStudents}명</p>
      </div>
      
      <div className="bg-white p-6 rounded-lg shadow">
        <h3 className="text-lg font-semibold">이번 달 매출</h3>
        <p className="text-3xl font-bold text-green-600">
          {stats.monthlyRevenue.toLocaleString()}원
        </p>
      </div>

      <div className="bg-white p-6 rounded-lg shadow">
        <h3 className="text-lg font-semibold">수업 현황</h3>
        <p className="text-3xl font-bold text-purple-600">{stats.totalClasses}개</p>
      </div>

      <div className="bg-white p-6 rounded-lg shadow">
        <h3 className="text-lg font-semibold">과목별 분포</h3>
        {Object.entries(stats.subjectDistribution).map(([subject, count]) => (
          <div key={subject} className="flex justify-between items-center mt-2">
            <span>{subject}</span>
            <span className="font-semibold">{count}명</span>
          </div>
        ))}
      </div>
    </div>
  );
};

export default DashboardOverview;
