import React from 'react';
import { useLocalStorage } from '../../hooks/useLocalStorage';

const DashboardOverview = () => {
  const [classes] = useLocalStorage('classes', []);
  const [students] = useLocalStorage('students', []);

  const calculateStats = () => {
    const subjectDistribution = students.reduce((acc, student) => {
      acc[student.subject] = (acc[student.subject] || 0) + 1;
      return acc;
    }, {});

    return {
      totalClasses: classes.length,
      subjectDistribution
    };
  };

  const stats = calculateStats();

  return (
    <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
      <div className="bg-white p-6 rounded-lg shadow">
        <h3 className="text-lg font-semibold">수업 현황</h3>
        <p className="text-3xl font-bold text-purple-600">{stats.totalClasses}개</p>
      </div>

      <div className="bg-white p-6 rounded-lg shadow">
        <h3 className="text-lg font-semibold">과목별 분포</h3>
        {Object.entries(stats.subjectDistribution).map(([subject, count]) => (
          <div key={subject} className="flex justify-between items-center mt-2">
            <span>{subject}</span>
            <span className="font-semibold">{count}명</span>
          </div>
        ))}
      </div>
    </div>
  );
};

export default DashboardOverview;
