import React, { useState, useEffect } from 'react';

const ClassSchedule = () => {
  const [classes, setClasses] = useState([]);
  const [selectedDate, setSelectedDate] = useState(new Date().toISOString().split('T')[0]);

  useEffect(() => {
    const loadedClasses = JSON.parse(localStorage.getItem('classes') || '[]');
    setClasses(loadedClasses);
  }, []);

  const filteredClasses = classes.filter(
    cls => cls.classDate === selectedDate
  ).sort((a, b) => a.classTime.localeCompare(b.classTime));

  const handleDelete = (id) => {
    if (window.confirm('수업을 삭제하시겠습니까?')) {
      const updatedClasses = classes.filter(cls => cls.id !== id);
      localStorage.setItem('classes', JSON.stringify(updatedClasses));
      setClasses(updatedClasses);
    }
  };

  return (
    <div className="bg-white p-6 rounded-lg shadow">
      <h2 className="text-xl font-bold mb-4">수업 시간표</h2>
      <div className="mb-4">
        <input
          type="date"
          value={selectedDate}
          onChange={(e) => setSelectedDate(e.target.value)}
          className="w-full p-2 border rounded"
        />
      </div>
      <div className="space-y-4">
        {filteredClasses.length === 0 ? (
          <p className="text-gray-500">예약된 수업이 없습니다.</p>
        ) : (
          filteredClasses.map(cls => (
            <div key={cls.id} className="border p-4 rounded">
              <div className="flex justify-between items-start">
                <div>
                  <p className="font-bold">{cls.studentName}</p>
                  <p className="text-sm text-gray-600">{cls.subject}</p>
                  <p className="text-sm text-gray-600">
                    {cls.classTime} ({cls.duration}분)
                  </p>
                </div>
                <button
                  onClick={() => handleDelete(cls.id)}
                  className="text-red-500 hover:text-red-700"
                >
                  삭제
                </button>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default ClassSchedule;
