import React from 'react';
import { Link, useLocation } from 'react-router-dom';

const Navbar = () => {
  const location = useLocation();
  
  const isActive = (path) => {
    return location.pathname === path ? 'text-blue-600 font-medium' : 'text-gray-600 hover:text-blue-500 transition-colors';
  };

  return (
    <nav className="bg-white/80 backdrop-blur-md shadow-md sticky top-0 z-50">
      <div className="container mx-auto px-4 max-w-7xl">
        <div className="flex justify-between items-center h-16">
          <Link to="/" className="text-2xl font-bold text-blue-600 hover:text-blue-700 transition-colors">
            음악학원 관리
          </Link>
          <div className="flex space-x-8">
            <Link to="/student-registration" className={`${isActive('/student-registration')} px-3 py-2 rounded-lg hover:bg-blue-50`}>
              학생등록
            </Link>
            <Link to="/student-management" className={`${isActive('/student-management')} px-3 py-2 rounded-lg hover:bg-blue-50`}>
              학생관리
            </Link>
            <Link to="/class-management" className={`${isActive('/class-management')} px-3 py-2 rounded-lg hover:bg-blue-50`}>
              수업관리
            </Link>
            <Link to="/payment-management" className={`${isActive('/payment-management')} px-3 py-2 rounded-lg hover:bg-blue-50`}>
              결제관리
            </Link>
          </div>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
