import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { Bars3Icon, XMarkIcon } from '@heroicons/react/24/outline';

const Layout = ({ children }) => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  const navItems = [
    { title: '대시보드', path: '/', className: 'text-blue-600 hover:text-blue-800' },
    { title: '학생등록', path: '/student-registration', className: 'text-indigo-600 hover:text-indigo-800' },
    { title: '학생관리', path: '/student-management', className: 'text-gray-600 hover:text-gray-800' },
    { title: '수업관리', path: '/class-management', className: 'text-purple-600 hover:text-purple-800' },
    { title: '결제관리', path: '/payment-management', className: 'text-blue-600 hover:text-blue-800' },
  ];

  const handleToggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  return (
    <div className="min-h-screen bg-gray-50">
      <nav className="bg-white/90 backdrop-blur-lg border-b border-gray-200 sticky top-0 z-40">
        <div className="max-w-[1200px] px-6 mx-auto flex flex-col items-center py-4">
          <Link 
            to="/" 
            className="text-3xl font-bold text-transparent bg-clip-text bg-gradient-to-r from-blue-600 to-purple-600 tracking-tight hover:from-blue-700 hover:to-purple-700 transition-all mb-4"
          >
            MUSIC ACADEMY
          </Link>

          {/* Desktop Navigation */}
          <div className="hidden md:flex items-center space-x-12">
            {navItems.map(item => (
              <Link
                key={item.path}
                to={item.path}
                className={`${item.className} text-lg font-medium transition-colors`}
              >
                {item.title}
              </Link>
            ))}
          </div>

          {/* Mobile menu button */}
          <button
            className="md:hidden absolute right-4 top-4 p-2"
            onClick={handleToggleMenu}
          >
            {isMenuOpen ? (
              <XMarkIcon className="h-6 w-6" />
            ) : (
              <Bars3Icon className="h-6 w-6" />
            )}
          </button>
        </div>

        {/* Mobile Navigation */}
        {isMenuOpen && (
          <div className="md:hidden border-t">
            <div className="container px-4 py-3">
              {navItems.map(item => (
                <Link
                  key={item.path}
                  to={item.path}
                  className={`block py-2 ${item.className} text-lg`}
                  onClick={() => setIsMenuOpen(false)}
                >
                  {item.title}
                </Link>
              ))}
            </div>
          </div>
        )}
      </nav>

      <main className="container mx-auto px-4 py-8">
        {children}
      </main>
    </div>
  );
};

export default Layout;
