import React from 'react';
import TodayClasses from '../components/dashboard/TodayClasses';
import RecentStudents from '../components/dashboard/RecentStudents';
import PaymentStatus from '../components/dashboard/PaymentStatus';
import StudentCount from '../components/dashboard/StudentCount';

const Dashboard = () => {
  return (
    <div>
      <h1 className="text-3xl font-bold bg-gradient-to-r from-blue-600 to-indigo-600 bg-clip-text text-transparent mb-8">
        대시보드
      </h1>
      <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
        {[
          { component: <StudentCount />, key: 'count' },
          { component: <TodayClasses />, key: 'classes' },
          { component: <RecentStudents />, key: 'students' },
          { component: <PaymentStatus />, key: 'payments' }
        ].map(({ component, key }) => (
          <div 
            key={key}
            className="transform hover:scale-[1.02] hover:-translate-y-1 transition-all duration-300"
          >
            {component}
          </div>
        ))}
      </div>
    </div>
  );
};

export default Dashboard;
